mapper.select:
SELECT RE.ID,RE.NAME,RE.PARENT_ID,RE.URL FROM RESOURCE RE LEFT JOIN ROLE_RESOURCE RR ON RE.ID = RR.RESOURCE_ID LEFT JOIN USER_ROLE UR ON RR.ROLE_ID =UR.ROLE_ID WHERE UR.USER_ID=#{userid}
AND RE.TYPE= #{type}
GROUP BY RE.ID ORDER BY RE.SORT ASC

mapper.insert:
insert into RESOURCE (ID, `NAME`, URL, 
  `TYPE`, PARENT_ID, SORT
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{type,jdbcType=INTEGER}, #{parentId,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}
  )
